const items = $input.all();
const firstItem = items[0];

// Handle both test mode (direct JSON) and production (form-encoded)
let payload;
if (firstItem.json.body && firstItem.json.body.payload) {
  payload = JSON.parse(firstItem.json.body.payload);
} else if (firstItem.json.type) {
  payload = firstItem.json;
} else {
  throw new Error("Unable to parse Slack payload");
}

const payloadType = payload.type;
const user = payload.user;
const triggerId = payload.trigger_id;

const output = {
  payloadType,
  triggerId,
  userId: user?.id,
  userName: user?.username || user?.name,
  rawPayload: payload,
};

// Button clicks (block_actions)
if (payloadType === "block_actions" && payload.actions?.length > 0) {
  const action = payload.actions[0];
  output.actionId = action.action_id;
  output.draftId = action.value;
  output.channelId = payload.channel?.id;
  output.messageTs = payload.message?.ts;
  output.responseUrl = payload.response_url;
}

// Modal submissions (view_submission)
if (payloadType === "view_submission") {
  const view = payload.view;
  output.callbackId = view?.callback_id;

  if (view?.private_metadata) {
    try {
      output.privateMetadata = JSON.parse(view.private_metadata);
    } catch (e) {
      output.privateMetadata = { raw: view.private_metadata };
    }
  }

  const values = view?.state?.values;
  if (values?.feedback_input?.feedback_text?.value) {
    output.feedbackText = values.feedback_input.feedback_text.value;
  }
}

return [{ json: output }];

const payload = $('Parse Slack Payload').first().json;

const rejectionRecord = {
  draftId: payload.draftId,
  status: 'rejected',
  rejectedAt: new Date().toISOString(),
  rejectedBy: {
    userId: payload.userId,
    userName: payload.userName
  },
  draftPath: `/data/shared/output/drafts/${payload.draftId}.json`,
  responseUrl: payload.responseUrl,
  channelId: payload.channelId,
  messageTs: payload.messageTs
};

return [{ json: rejectionRecord }];

