}];

const brandGuidelines = $input.first().json.data;
const context = $('Build Revision Context').first().json;

const prompt = `You are a senior copywriter for Bentley Motors. You previously generated social media content that needs revision.

## Brand Voice Guidelines (excerpt)
${brandGuidelines.substring(0, 2500)}

## Original Content
**Platform:** ${context.platform}
**Theme:** ${context.theme}
**Vehicle:** ${context.vehicle}

**Headline:** ${context.originalHeadline}
**Body Copy:** ${context.originalBodyCopy}
**Call to Action:** ${context.originalCta}
**Hashtags:** ${context.originalHashtags}

## Reviewer Feedback
"${context.feedbackText}"

## Your Task
Generate a revised version addressing the feedback while:
- Maintaining Bentley's brand voice (sophisticated, confident, understated luxury)
- Using British English spelling throughout
- Avoiding exclamation marks entirely
- Keeping within platform character limits (Instagram: 2200 chars max)
- Using exactly 5 hashtags from: #BentleyMotors, #Craftsmanship, #ContinentalGT, #FlyingSpur, #Bentayga, #BritishLuxury, #Handcrafted, #ExtraordinaryJourneys, #BentleyLife, #Performance

## Output Format
Return ONLY valid JSON:
{
  "headline": "Your revised headline",
  "bodyCopy": "Your revised body copy",
  "cta": "Your revised call to action",
  "hashtags": ["#Tag1", "#Tag2", "#Tag3", "#Tag4", "#Tag5"],
  "revisionNotes": "Brief explanation of changes"
}`;

return [{ json: { prompt, context } }];

const inputData = $input.first().json;

// Handle different Anthropic response structures
let response;
if (inputData.message?.content) {
  response = inputData.message.content;
} else if (inputData.content?.[0]?.text) {
  response = inputData.content[0].text;
} else {
  throw new Error('Unable to find Claude response content');
}

const context = $('Build Revision Prompt').first().json.context;

let jsonStr = response;
if (response.includes('```json')) {
  jsonStr = response.replace(/```json\n?/g, '').replace(/```\n?/g, '');
} else if (response.includes('```')) {
  jsonStr = response.replace(/```\n?/g, '');
}
jsonStr = jsonStr.trim();

const parsed = JSON.parse(jsonStr);

const totalChars = (parsed.headline || '').length +
  (parsed.bodyCopy || '').length +
  (parsed.cta || '').length +
  (parsed.hashtags || []).join(' ').length;

const now = new Date();
const revisedDraft = {
  draftId: context.newDraftId,
  createdAt: now.toISOString(),
  status: 'pending_review',
  sourcePackageId: context.originalDraft.sourcePackageId,
  params: context.originalDraft.params,
  image: context.originalDraft.image,
  generatedContent: {
    headline: parsed.headline,
    bodyCopy: parsed.bodyCopy,
    cta: parsed.cta,
    hashtags: parsed.hashtags,
    totalCharacters: totalChars
  },
  validation: {
    isValid: true,
    errors: [],
    warnings: [],
    totalCharacters: totalChars,
    bodyCharacters: (parsed.bodyCopy || '').length,
    hashtagCount: (parsed.hashtags || []).length
  },
  formattedPost: [
    parsed.headline,
    '',
    parsed.bodyCopy,
    '',
    parsed.cta,
    '',
    (parsed.hashtags || []).join(' ')
  ].join('\n'),
  revision: {
    revisionOf: context.originalDraftId,
    revisionNumber: context.newVersion,
    feedbackReceived: context.feedbackText,
    revisionNotes: parsed.revisionNotes,
    revisedBy: context.userName
  }
};

const fileContent = [{
  draft: revisedDraft,
  filename: `${context.newDraftId}.json`,
  filePath: `/data/shared/output/drafts/${context.newDraftId}.json`
}];

return [{
